import 'package:flutter/material.dart';import 'package:tellesports/core/app_export.dart';import 'package:tellesports/widgets/app_bar/appbar_subtitle_one.dart';import 'package:tellesports/widgets/app_bar/custom_app_bar.dart';import 'package:tellesports/widgets/custom_elevated_button.dart';class PredictionsPage extends StatelessWidget {const PredictionsPage({Key? key}) : super(key: key);

@override Widget build(BuildContext context) { mediaQueryData = MediaQuery.of(context); return SafeArea(child: Scaffold(appBar: _buildAppBar(context), body: Container(width: double.maxFinite, padding: EdgeInsets.symmetric(horizontal: 20.h, vertical: 24.v), child: Column(children: [_buildAvatarFrame(context), SizedBox(height: 24.v), _buildSettingsFrame(context), SizedBox(height: 24.v), _buildShareFrame(context, text: "Contact support", image: ImageConstant.imgHelpCenter), SizedBox(height: 24.v), _buildShareFrame(context, text: "Share Tellasport", image: ImageConstant.imgShareGray700), SizedBox(height: 24.v), CustomElevatedButton(text: "Log out", buttonStyle: CustomButtonStyles.fillRedTL8, onPressed: () {onTapLogOut(context);}), SizedBox(height: 5.v)])))); } 
