Widget _buildSettingsFrame(BuildContext context) { return Container(padding: EdgeInsets.symmetric(horizontal: 8.h, vertical: 16.v), decoration: AppDecoration.outlineBlack9001.copyWith(borderRadius: BorderRadiusStyle.roundedBorder8), child: Column(mainAxisSize: MainAxisSize.min, children: [_buildChatSettingsFrame(context, text: "Notifications settings"), SizedBox(height: 12.v), Divider(color: appTheme.gray50001), SizedBox(height: 11.v), _buildChatSettingsFrame(context, text: "Chat settings")])); } 
